// 1. Daten aus der Gemini/LangChain Struktur extrahieren
const input = $input.item.json;
let rawText = "";

if (input.content && input.content.parts && input.content.parts[0]) {
  rawText = input.content.parts[0].text;
} else if (input.text) {
  rawText = input.text;
} else {
  rawText = JSON.stringify(input);
}

// 2. JSON-Block aus dem Text extrahieren (falls Markdown-Backticks vorhanden sind)
let parsedData;
try {
  const jsonMatch = rawText.match(/\{[\s\S]*\}/);
  if (!jsonMatch) throw new Error("Kein JSON im Text gefunden");
  parsedData = JSON.parse(jsonMatch[0]);
} catch (e) {
  return {
    json: {
      positionen: [{ titel: "Fehler beim Lesen der KI-Daten", menge: 0, einheit: "-", einzelpreis: 0, summe: 0 }],
      gesamt_netto: 0,
      ki_analyse_notiz: "Die KI-Antwort konnte nicht gelesen werden.",
      kunde: "Fehler",
      datum: new Date().toLocaleDateString('de-DE')
    }
  };
}

// 3. Positionen mappen (Flexibel fÃ¼r GroÃŸ-/Kleinschreibung)
const rawPositions = parsedData.positionen || parsedData.Positionen || parsedData.Leistungen || [];

const cleanedPositionen = rawPositions.map(pos => {
  const m = parseFloat(pos.menge || pos.Menge || 0);
  const p = parseFloat(pos.einzelpreis || pos.Einzelpreis || 0);
  const s = parseFloat(pos.summe || pos.Summe || (m * p));
  return {
    titel: pos.titel || pos.Titel || pos.Bezeichnung || "Leistung",
    menge: m,
    einheit: pos.einheit || pos.Einheit || "mÂ²",
    einzelpreis: Math.round(p * 100) / 100,
    summe: Math.round(s * 100) / 100
  };
});

// 4. Ergebnis fÃ¼r "Antwort senden" vorbereiten
return {
  json: {
    positionen: cleanedPositionen,
    gesamt_netto: Math.round(cleanedPositionen.reduce((acc, curr) => acc + curr.summe, 0) * 100) / 100,
    ki_analyse_notiz: parsedData.ki_analyse_notiz || parsedData.Hinweise || "Kalkulation erfolgreich.",
    kunde: parsedData.Kunde || "Kunde",
    datum: new Date().toLocaleDateString('de-DE')
  }
};

