// ============================================
// SCHRITT 1: Eingabedaten Validierung & Prep
// ============================================

const body = $input.item.json.body || {};
const binary = $input.item.binary || {};

// Extrahiere Textdaten
const customerName = body.customerName || 'Unbekannter Kunde';
const address = body.address || '';
const notes = body.notes || '';

// Verarbeite hochgeladene Medien
let mediaFiles = [];
let imageCount = 0;
let videoCount = 0;

for (const [key, file] of Object.entries(binary)) {
  const mimeType = file.mimeType || '';

  if (mimeType.startsWith('image/')) {
    mediaFiles.push({
      type: 'image',
      key: key,
      fileName: file.fileName || `image_${imageCount}`,
      mimeType: mimeType,
      size: file.fileSize || 0
    });
    imageCount++;
  } else if (mimeType.startsWith('video/')) {
    mediaFiles.push({
      type: 'video',
      key: key,
      fileName: file.fileName || `video_${videoCount}`,
      mimeType: mimeType,
      size: file.fileSize || 0
    });
    videoCount++;
  }
}

// Bestimme Analyse-Route
let routeType = 'text_only';
if (imageCount > 0 && videoCount === 0) {
  routeType = 'image_analysis';
} else if (videoCount > 0 && imageCount === 0) {
  routeType = 'video_analysis';
} else if (imageCount > 0 && videoCount > 0) {
  routeType = 'mixed_media';
}

return {
  json: {
    customerName,
    address,
    notes,
    mediaFiles,
    imageCount,
    videoCount,
    totalMediaCount: mediaFiles.length,
    routeType,
    timestamp: new Date().toISOString()
  },
  binary: binary
};
